import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

interface ReportModalProps {
  isOpen: boolean;
  onClose: () => void;
  sessionId: string | null;
}

const REPORT_REASONS = [
  { value: "spam", label: "Spam" },
  { value: "harassment", label: "Harassment" },
  { value: "fake-location", label: "Fake location" },
  { value: "inappropriate", label: "Inappropriate content" },
  { value: "other", label: "Other" },
];

export function ReportModal({ isOpen, onClose, sessionId }: ReportModalProps) {
  const [reason, setReason] = useState("");
  const [details, setDetails] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!reason || !sessionId) return;

    setIsSubmitting(true);

    // Simulate API call
    console.log("Report submitted:", { sessionId, reason, details });

    setTimeout(() => {
      toast({
        title: "Report submitted",
        description: "Thanks ‚Äî a moderator will review.",
      });
      setIsSubmitting(false);
      setReason("");
      setDetails("");
      onClose();
    }, 500);
  };

  const handleClose = () => {
    setReason("");
    setDetails("");
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md" data-testid="modal-report">
        <DialogHeader>
          <DialogTitle>Report Session</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="reason">Reason *</Label>
            <Select value={reason} onValueChange={setReason} required>
              <SelectTrigger data-testid="select-report-reason">
                <SelectValue placeholder="Select reason..." />
              </SelectTrigger>
              <SelectContent>
                {REPORT_REASONS.map((option) => (
                  <SelectItem key={option.value} value={option.value}>
                    {option.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label htmlFor="details">Details (Optional)</Label>
            <Textarea
              id="details"
              value={details}
              onChange={(e) => setDetails(e.target.value)}
              placeholder="Please provide more details..."
              maxLength={300}
              rows={4}
              data-testid="textarea-report-details"
            />
            <p className="text-xs text-muted-foreground">
              {details.length}/300 characters
            </p>
          </div>

          <div className="flex gap-3">
            <Button
              type="submit"
              disabled={!reason || isSubmitting}
              className="flex-1 bg-destructive text-destructive-foreground hover:bg-destructive/90"
              data-testid="button-submit-report"
            >
              {isSubmitting ? "Submitting..." : "Submit Report"}
            </Button>
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              data-testid="button-cancel-report"
            >
              Cancel
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/SessionCard.tsx.content:
import { Clock, MoreVertical } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Session } from "@/types";
import { useStore } from "@/store/useStore";

interface SessionCardProps {
  session: Session;
  onReport: (sessionId: string) => void;
}

export function SessionCard({ session, onReport }: SessionCardProps) {
  const { joinedSessions, joinSession, leaveSession } = useStore();
  const isJoined = joinedSessions.has(session.id);
  const isFull = session.attendeeCount >= session.capacity;
  const progressPercent = (session.attendeeCount / session.capacity) * 100;

  const handleToggleJoin = async () => {
    try {
      if (isJoined) {
        await leaveSession(session.id);
      } else if (!isFull) {
        await joinSession(session.id);
      }
    } catch (error) {
      console.error('Error toggling session join status:', error);
    }
  };

  return (
    <Card className="p-6 hover:shadow-md transition-shadow" data-testid={`card-session-${session.id}`}>
      <div className="flex justify-between items-start mb-4">
        <div className="space-y-2">
          <div className="flex items-center space-x-2">
            <Badge variant="default" className="bg-primary text-primary-foreground">
              {session.courseCode}
            </Badge>
            <Badge variant="secondary" className="bg-muted text-muted-foreground">
              {session.building} Floor {session.floor}
            </Badge>
            {!session.public && (
              <Badge variant="outline" className="bg-yellow-100 text-yellow-800 border-yellow-300">
                Private
              </Badge>
            )}
          </div>
          <div className="flex items-center space-x-1 text-sm text-muted-foreground">
            <Clock className="w-4 h-4" />
            <span data-testid={`text-ends-${session.id}`}>Ends {session.endsAt}</span>
          </div>
        </div>
        <div className="flex items-center space-x-2">
          <Avatar className="w-8 h-8">
            <AvatarFallback className="bg-primary text-primary-foreground text-sm">
              {session.host.initials}
            </AvatarFallback>
          </Avatar>
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="icon" className="h-8 w-8" data-testid={`button-menu-${session.id}`}>
                <MoreVertical className="w-4 h-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem 
                onClick={() => onReport(session.id)}
                data-testid={`button-report-${session.id}`}
                className="text-destructive"
              >
                Report Session
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>

      <div className="mb-4">
        <div className="flex justify-between items-center mb-2">
          <span className="text-sm font-medium text-foreground">Capacity</span>
          <span className="text-sm text-muted-foreground" data-testid={`text-capacity-${session.id}`}>
            {session.attendeeCount}/{session.capacity}
          </span>
        </div>
        <Progress value={progressPercent} className="h-2" />
      </div>

      {session.notes && (
        <p className="text-sm text-muted-foreground mb-4 italic" data-testid={`text-notes-${session.id}`}>
          "{session.notes}"
        </p>
      )}

      {session.locationImages && session.locationImages.length > 0 && (
        <div className="mb-4">
          <p className="text-xs text-muted-foreground mb-2">üìç Location Photos:</p>
          <div className="grid grid-cols-3 gap-2">
            {session.locationImages.slice(0, 3).map((imageUrl, index) => (
              <div 
                key={`${session.id}-image-${index}`} 
                className="relative aspect-square rounded-md overflow-hidden bg-muted"
              >
                <img
                  src={imageUrl}
                  alt={`Study location ${index + 1}`}
                  className="w-full h-full object-cover hover:scale-105 transition-transform duration-200"
                  data-testid={`location-image-${session.id}-${index}`}
                />
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="flex justify-between items-center">
        <span className="text-sm text-muted-foreground" data-testid={`text-host-${session.id}`}>
          Host: {session.host.name}
        </span>
        <Button
          onClick={handleToggleJoin}
          disabled={isFull && !isJoined}
          variant={isJoined ? "outline" : "default"}
          size="sm"
          data-testid={`button-join-${session.id}`}
          className={
            isJoined 
              ? "border-muted-foreground text-muted-foreground hover:bg-muted" 
              : isFull 
                ? "bg-muted text-muted-foreground cursor-not-allowed" 
                : "bg-primary text-primary-foreground hover:opacity-90"
          }
        >
          {isJoined ? "Leave" : isFull ? "Full" : "Join"}
        </Button>
      </div>
    </Card>
  );
}

client/src/components/StripeCheckout.tsx.content:
import { useState, useEffect } from "react";
import { loadStripe } from "@stripe/stripe-js";
import {
  Elements,
  PaymentElement,
  useStripe,
  useElements
} from "@stripe/react-stripe-js";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

// Use testing public key if available, fallback to regular public key
const stripePublicKey = import.meta.env.TESTING_VITE_STRIPE_PUBLIC_KEY || import.meta.env.VITE_STRIPE_PUBLIC_KEY;

if (!stripePublicKey) {
  throw new Error('Missing required Stripe public key');
}

const stripePromise = loadStripe(stripePublicKey);

interface CheckoutFormProps {
  amount: number;
  onSuccess: () => void;
  onCancel: () => void;
}

function CheckoutForm({ amount, onSuccess, onCancel }: CheckoutFormProps) {
  const stripe = useStripe();
  const elements = useElements();
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    event.stopPropagation();

    if (!stripe || !elements) {
      console.log("Stripe not ready");
      return;
    }

    setIsLoading(true);

    try {
      const { error, paymentIntent } = await stripe.confirmPayment({
        elements,
        confirmParams: {
          return_url: `${window.location.origin}/donate?success=true`,
        },
        redirect: "if_required",
      });

      if (error) {
        console.error("Payment error:", error);
        toast({
          title: "Payment Failed",
          description: error.message || "There was an error processing your payment.",
          variant: "destructive",
        });
      } else if (paymentIntent && paymentIntent.status === "succeeded") {
        toast({
          title: "Thank you!",
          description: `Your $${amount} donation helps keep StudyBuddy free!`,
        });
        onSuccess();
      }
    } catch (err) {
      console.error("Payment processing error:", err);
      toast({
        title: "Payment Error",
        description: "An unexpected error occurred. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <PaymentElement />
      <div className="flex gap-3">
        <Button
          type="submit"
          disabled={!stripe || !elements || isLoading}
          className="flex-1 bg-primary text-primary-foreground hover:opacity-90"
        >
          {isLoading ? "Processing..." : `Donate $${amount}`}
        </Button>
        <Button
          type="button"
          variant="outline"
          onClick={onCancel}
          disabled={isLoading}
        >
          Cancel
        </Button>
      </div>
    </form>
  );
}

interface StripeCheckoutProps {
  amount: number;
  onSuccess: () => void;
  onCancel: () => void;
}

export function StripeCheckout({ amount, onSuccess, onCancel }: StripeCheckoutProps) {
  const [clientSecret, setClientSecret] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  useEffect(() => {
    // Create PaymentIntent when component mounts
    const createPaymentIntent = async () => {
      try {
        setIsLoading(true);
        setError(null);

        const response = await fetch("/api/create-payment-intent", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ amount }),
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();

        if (data.clientSecret) {
          setClientSecret(data.clientSecret);
        } else {
          throw new Error("No client secret received");
        }
      } catch (err) {
        console.error("Payment setup error:", err);
        const errorMessage = err instanceof Error ? err.message : "Unknown error";
        setError(errorMessage);
        toast({
          title: "Payment Setup Failed",
          description: "Failed to initialize payment. Please try again.",
          variant: "destructive",
        });
      } finally {
        setIsLoading(false);
      }
    };

    createPaymentIntent();
  }, [amount, toast]);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
        <p className="mt-2 text-sm text-muted-foreground">Setting up payment...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-4 text-center space-y-4">
        <p className="text-sm text-destructive">Payment setup failed: {error}</p>
        <Button onClick={onCancel} variant="outline">Go Back</Button>
      </div>
    );
  }

  if (!clientSecret) {
    return (
      <div className="p-4 text-center space-y-4">
        <p className="text-sm text-muted-foreground">Unable to setup payment</p>
        <Button onClick={onCancel} variant="outline">Go Back</Button>
      </div>
    );
  }

  return (
    <Elements stripe={stripePromise} options={{ clientSecret }}>
      <CheckoutForm amount={amount} onSuccess={onSuccess} onCancel={onCancel} />
    </Elements>
  );
}

